import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import axios from 'axios';

import Login from './components/Login';
import Preferences from './components/Preferences';
import Contests from './components/Contests';
import Account from './components/Account';

function App() {
    // State to store the user object, or null if not logged in
    const [user, setUser] = useState(null);

    // On mount, fetch user info to see if they're logged in
    useEffect(() => {
        async function fetchUser() {
            try {
                const res = await axios.get('http://localhost:5001/api/user/info', {
                    withCredentials: true
                });
                setUser(res.data);
            } catch (error) {
                // If 401 or user not found, set user to null
                setUser(null);
            }
        }
        fetchUser();
    }, []);

    // Sign out handler
    const handleLogout = async () => {
        try {
            await axios.get('http://localhost:5001/auth/logout', { withCredentials: true });
            setUser(null);
        } catch (error) {
            console.error("Logout failed:", error);
        }
    };

    return (
        <Router>
            <nav style={{ padding: '10px', background: '#eee' }}>
                <ul style={{ listStyle: 'none', display: 'flex', gap: '1rem' }}>
                    {/* If logged in, disable the Login link and show "Already logged in" + email + sign out */}
                    {user ? (
                        <>
                            {/* Make the login link unclickable (pointerEvents: 'none') */}
                            <li style={{ pointerEvents: 'none', color: 'gray' }}>
                                Already logged in
                            </li>
                            <li>{user.email}</li>
                            <li><Link to="/account">Account</Link></li>
                            <li>
                                <button onClick={handleLogout}>Sign Out</button>
                            </li>
                        </>
                    ) : (
                        // If not logged in, show a clickable login link
                        <li>
                            <Link to="/">Login</Link>
                        </li>
                    )}

                    <li><Link to="/preferences">Preferences</Link></li>
                    <li><Link to="/contests">Contests</Link></li>
                </ul>
            </nav>

            <Routes>
                <Route path="/" element={<Login />} />
                <Route path="/preferences" element={<Preferences />} />
                <Route path="/contests" element={<Contests />} />
                <Route path="/account" element={<Account />} />
            </Routes>
        </Router>
    );
}

export default App;
